ruleToc.chapterList:
+<js>

var url = book.tocUrl;
// 使用正则表达式匹配数字
var num= url.match(/\/book\/(\d+)\/index\.html/);

var bookId =num[1]; 
var baseUrl = "https://69shux.net/ajax_novels/chapterlist/";
// 使用提取的bookId拼接成新的URL
var newUrl = baseUrl + bookId + ".html";

java.log(newUrl );
res =java.ajax(newUrl );
result=res;

list=[];

var regex = /<a[^>]*>([^<]+)<\/a>/g;
var matches = [];
var match;
while ((match = regex.exec(result)) !== null) {
    // 将每次匹配到的数字（data-num的值）添加到 matches 数组中
    matches.push(match[1]);
}

// 使用正则表达式匹配 href= 后面的URL
var regex1 = /href="([^"]*)"/g;
var matches1 = [];
var match1;

while ((match1 = regex1.exec(result)) !== null) {
    // 将每次匹配到的URL（href的值）添加到 matches 数组中
    matches1.push(match1[1]);
}


for(let i=0;i<matches.length;i++){
list.push({ text:matches[i],href:matches1[i]});
}
list;
</js>

