exploreUrl:
@js:let prefix = [
    {n:"📚全本", p:"quanben", s:"_"},
    {n:"🌞总榜", p:"ph/all", s:"_"},
    {n:"🌙月榜", p:"ph/month", s:"_"},
    {n:"⭐周榜", p:"ph/week", s:"_"},
    {n:"⚡分类", p:"xclass/", s:"/"},
]

let types = [
    {n:"  全部  ", p:"0"},
    {n:"📔玄幻", p:"1"},
    {n:"📕修真", p:"2"},
    {n:"📗都市", p:"3"},
    {n:"📘穿越", p:"4"},
    {n:"📙网游", p:"5"},
    {n:"📒科幻", p:"6"},
    {n:"📔其他", p:"7"},
]
let titleStyle = {
    layout_flexGrow: 0,
    layout_flexShrink: 1,
    layout_alignSelf: "auto",
    layout_flexBasisPercent: 1,
    layout_wrapBefore: false
};
let defaultStyle = {
    layout_flexBasisPercent: .28
};
let result = [];
for (let pre of prefix) {
    let title = `🔹 ${pre.n} 🔹`;
    result.push({
        title: title,
        url: "",
        style: titleStyle
    });
    for (let t of types) {
        result.push({
            title: t.n,
            url: `/${pre.p}${t.p}${pre.s}{{page}}.html`,
            style: defaultStyle
        });
    }
}

JSON.stringify(result);

ruleBookInfo.kind:
class.synopsisArea_detail@tag.p.1:4@text@js:let T = '类别：';
let U = '更新：';
let r = [];
for (let i = 0; i < result.length; i++) {
    let item = result[i];
    if (item.startsWith(T)) {
        item = item.substring(T.length);
    } else if (item.startsWith(U)) {
        item = item.substring(U.length).replace('T', ' ');
    }
    r.push(item);
}r

