@js:
sort=[];
push=(title,url,type)=>{
		json = JSON.stringify({
				title: title,
				url: url?url:"",
				style: {
						layout_flexGrow: 1,
						layout_flexBasisPercent: type
					}
			});
		return sort.push(json);
	}
push('排行榜单',null,1);
$$=(a,b,c)=>{
		return `https://reader.browser.duokan.com/api/v2/ranking?len=10&page={{page}\}&type=${a}-${b}-${c}`
	}
[["人气榜","golden"],["新书榜","new"],["完结榜","end"],["热搜榜","search"]].map([title,b]=>{
		push(''+title+'',null,1);
		["男频","女频"].map((title1,a)=>{
				a++
				[["周榜","week"],["月榜","month"],["总榜","total"]].map([title2,c]=>{
						return push(title1+title2,$$(a,b,c),0.25);
					});
			});
	});
push('书籍分类',null,1);
$$=(sex,bookStatus,categoryId,hotChoice)=>{
		return `https://reader.browser.duokan.com/api/v2/book/list2?len=10&page={{page}\}&sex=${sex}&bookStatus=${bookStatus}&categoryId=${categoryId}&wordCountsInterval=0&hotChoice=${hotChoice}`
	}
bookStatus=categoryId=hotChoice=0;
[
		["男频",[["都市",7],["玄幻",3],["仙侠",6],["历史",8],["灵异",10],["科幻",11],["奇幻",4],["同人",143],["游戏",12],["武侠",5],["竞技",13]]],
		["女频",[["现代言情",75],["古代言情",74],["浪漫青春",76],["玄幻言情",72],["仙侠奇缘",73],["悬疑灵异",77],["科幻空间",78],["同人小说",80],["游戏竞技",79],["耽美小说",81]]]
].map(([title,list],sex)=>{
		sex++
		push(''+title+'',$$(sex,bookStatus,categoryId,hotChoice),1);
		list.map([title,categoryId]=>{
				push(''+title+'',$$(sex,bookStatus,categoryId,hotChoice),1);
				["综合全部","最新上线","阅读最多","点赞最高"].map((title,hotChoice)=>{
						['['+title+']',"连载","完结"].map((title,bookStatus)=>{
								return push(title,$$(sex,bookStatus,categoryId,hotChoice),0.25);
							});
					});
			});
	});
'['+sort.toString()+']'

