@js:
if (baseUrl != java.redirectUrl) {
	//重定向 重新设置书籍地址
	java.setBaseUrl(java.redirectUrl)
	//java.getBook().bookUrl = java.redirectUrl
	book.bookUrl = java.redirectUrl
}
result;
var bv=book.getVariable("custom");
if(!bv||bv==""){
	//java.log("未设置书源接口，默认1")
	v=1-1;
	}else{
		v=Number(bv)-1;
		}
//	java.put("v",v)
//result;
j=org.jsoup.Jsoup.parse(result);
$=e=>j.select(e);
dec=u=>decodeURIComponent(u);

intro=v=>{
	var lis=$("li.site"),list=[],
	txt=lis.eq(v).select("a").eq(1).text(),
	h=lis.eq(v).select("a").eq(1).attr("href");
	curr=`\n 当前源： 【${v+1}】
	\n章节目录来自：${txt}\n
	${dec(h).split("?u=")[1]}`;
	lisa=lis.toArray();
	lb=lisa.forEach((a,i)=>{
		name0=a.select("a").get(0).text()
		name1=a.select("a").get(1).text()
		u=a.select("a").get(1).attr("href")
		add=`【${i+1}】、${name0} 来自 ${name1}`;
		list.push(add)
		})
		tips="\n源列表：（多个源可设置书籍变量更改目录）\n"
		jie="\n————————————\n简介：\n"+$("#intro").html();
		jsa=curr+tips+list.join("\n")+jie;
		return jsa;
		}

link=v=>{
	var u=$("li.site").get(v)
	.select("a").eq(1).attr("href");
	return dec(u).split("?u=")[1];
	}

(function(){
	name=$("p.title").text();
	author=$("p.author").text();
	kind=$("p[class~=category|time]").text()
	last=$("p.newestChapter a").text()
	cover=$(".BGsectionOne-top-left img").attr("src");
	info={
		n:name,
		a:author,
		k:kind,
		l:last,
		i:intro(v),
		c:cover,
		u:link(v)
		};
	return info;
	})()

