try{
	d=(a,b)=>{
		b = java.md5Encode(b);
   var d = b.substring(0,16);
   var e = b.substring(16);
   type = "AES/CBC/PKCS7Padding";
  cipher=java.createSymmetricCrypto(
  	type,e,d);
   	return cipher.decryptStr(a);
   }
   code=src.match(/html\((d\(.+\))\)/)[1];
   //java.log(code)
		result=eval(code);
	}catch(e){
				java.log(e)
		java.longToast(book.name+title + ": 解密失败，改成动态解密");
		//如果解密出错，使用动态解密
		web=java.webView(null,baseUrl,null);
		java.setContent(web);
		content=java.getElement(
			".RBGsectionThree-content");
			result=content.html();
		}finally{
			result
			}

