if (source.bookSourceComment.match(/图片〖([\s\S]*?)〗/)[1] == "开") {
  p = `{{$..illustration.imageUrl}}`;
  img = p != "" ? `<img src="${p}">`: "";
} else {
  img = "";
};
art + img;

