@js:
sort = [];
push = (title, url, type) => sort.push({
  title: title,
  url: url,
  style: {
    layout_flexGrow: 1,
    layout_flexBasisPercent: type
  }
});
$ = (x) => `search?${x}&orderType=1&page={{page}}&searchType=1&size=36`;
u = source.key;
push("• 排行榜 •", null, 1);
["周榜", "月榜", "年榜", "总榜"].map((p, q) => {
  push(p, `rank?type=${q + 1}`, null);
});
["日本", "韩国"].map((o, r) => {
  g = r + 1;
  push(`【${o}H漫】`, $(`origin=` + g), 1);
  [["分类", "category"],
  ["标签", "includeTags"]].map([a, b] => {
    push(`• ${a} •`, $(""), 1);
    d = /y/.test(b) ? "categories": "tags";
    c = JSON.parse(java.ajax(u + d + `?type=` + g)).model;
    for (i = 0; i < c.length; i++) {
      push(
        c[i].name,
        $(b + "=" + encodeURIComponent(c[i].name)),
        null
      );
    };
  });
});
JSON.stringify(sort);
