arr1 = []; // 成功
arr2 = []; // 失败
arr3 = []; // 新增
if ($.time < time) {
    java.toast("检测更新中");
    try {
        for (i in $.domains) {
            arr1.push($.domains[i]);
            arr2.push($.domains[i]);
            getHost(i);
            try {
                getData(vuri); //校验搜索
