List = run("data").list;

Cipher = java.createSymmetricCrypto("AES/CBC/PKCS5Padding", aesKey, "0123456789abcdef");

List.map($$=>{
	path = Cipher.decryptStr($$.path);
try{
	path = String(path).split(_reg)[2];
}catch(err){}
	return {
		name: $$.chapterName,
		path: eurl(path),
		info: `章节字数：${$$.wordNum}　更新时间：${$$.updatedAt}`
		}
});

