ruleToc.chapterList:
@js:
let u = baseUrl.replace('list','content')

let v = [];
let list = [];
JSON.parse(result).data.list.map($=>{

//分卷判定
		V = $.volumeTitle
		if(v[v.length-1]!=V&&!/^\s*$/.test(V)){
				v.push(V)
				list.push({
						name: '📖['+V+']📖',
						volume: true
					})
			}

		return list.push({
				name: $.chapterName,
				url: u+'?chapterId='+$.chapterId+'&volumeId=1',
				info: '更新时间：'+java.timeFormatUTC($.updateTime*1000,'yyyy-MM-dd HH:mm',12)
			});
	});
v.length<2?list.filter($=>!$.volume):list

exploreUrl:
@js:
sort = cache.getFile('KuwoNovels');
if(sort==null){
var fk = sk = 1,
sort1 = [],
sort2 = [];
push=(title,url,type1,type2)=>{
		json = JSON.stringify({
				title: title,
				url: url?url:"",
				style: {
						layout_flexGrow: 1,
						layout_flexBasisPercent: type1
					}
			});
		return  eval('sort'+type2+'.push(json)');
	}
$$=(freetype,category_id,fk,sk)=>`http://appi.kuwo.cn/novels/api/book/category/${freetype}?category_id=${category_id}&fk=${fk}&sk=${sk}&pi={{page}\}&ps=20`;

JSON.parse(java.ajax('http://appi.kuwo.cn/novels/api/book/categories')).data.map($=>{
		freetype = $.freetype
		if(freetype==3)return;

		push('༺ˇ»`ʚ'+$.freetype_name+'ɞ´«ˇ༻', null, 1, freetype);
		$.categories.map($=>{
				category_id = $.category_id
				push('༺ '+$.category_name+' ༻', $$(freetype,category_id,sk,fk), 1, freetype);
				["综合", "热门", "最新"].map((title,sk)=>{
						sk++
						['['+title+']', '完结', '连载'].map((title,fk)=>{
								fk++
								push(title, $$(freetype,category_id,sk,fk), 0.25, freetype);
							});
					});
			});
	});
sort = sort1.concat(sort2);
cache.putFile('KuwoNovels', sort, 6E6);
}
'['+sort.toString()+']'

ruleSearch.bookList:
.grid tr!0
<js>
result.toArray().slice(0, 3).map(el => {
	var url = String(el.select('a:eq(0)').attr('href')).replace(/^\//, baseUrl.match(/(.+?\/\/[^\/]+\/)/)[1]);
	var J = org.jsoup.Jsoup.parse(java.ajax(url));
	var o = selector => String(J.select(selector).text());
	var og = selector => String(J.select('[property="og:' + selector + '"]').attr('content'));
	return {
		name: og('novel:book_name').replace(/(全文|小说|免费阅读|最新章节).*|[(（].*[）)]/g, ''),
		author: og('novel:author'),
		kind: og('novel:category') + ',' + og('novel:status').replace(/中|已/, ''),
		latest: og('novel:latest_chapter_name').replace(/^(正文|VIP章节|最新章节)?(\s+|_)|[\(（【].*[求更谢乐发推].*/g, '') + ' • ' + og('novel:update_time').replace(/(T|\s).*/, ' ').replace(/\//g, '-'),
		intro: og('description').replace(/.*(观看小说|简介)[:：]|各位书友.*|\s+/g, ''),
		cover: og('image'),
		url: url,
	};
}); 
</js>

