while(p_tmp>n3.lastIndex){
i=i-1;
p_tmp=p[i].w;
}
n_pre=p[i].q;
n_pre=n_pre+' | ';
}
//保存数据到数组
n=n_pre+n_sub;
t=t+1;
list.push({k:n,v:url[t]});
}
list;

