var url = source.key,
html = java.ajax(url);
token = org.jsoup.Jsoup.parse(html).select('input[name=_token]').attr('value');
body ="keyword={{key}}&page={{page}}&_token=" + token;
url += "/search,"
post = JSON.stringify({
  "body": String(body),
  "method": "POST"
});

