list=[]
lis=result
for(i in lis) {
    if(String(lis[i]).match(/href/)) {
        lis[i].select("a").forEach(a=>{
            list.push({
                text: a.text().match(/>(.*)/)[1],
                href: a.attr("href")
            })
        })
    } else {
        list.push({
            text: " 🏷️ "+lis[i].text(),
            vo: true
        })
    }
}
