exploreUrl:
@js:
u = source.bookSourceComment.match(/网址〖([^〗]*)〗/)[1];
sort = [];
push = (title, url, type) => sort.push({
  title: title,
  url: url,
  style: {
    layout_flexGrow: 1,
    layout_flexBasisPercent: type
  }
});
X = org.jsoup.Jsoup.parse(java.ajax(u)).select(".SHsectionTwo-top .SHsectionTwo-bottom-content li a[href*=/s/]");
for (i = 0; i < X.length; ++i) {
  push(X[i].text(), u + X[i].attr('href') + `/{{page}}/`, null)
};
JSON.stringify(sort);

ruleBookInfo.intro:
@js:
t = book.getVariable("custom");
t = t != "" ? t: 0;
J = org.jsoup.Jsoup.parse(result);
site = J.select('.site');
list = "";
for (i = 0; i < site.length; ++i) {
  T = (x) => site[i].select(x).text();
  list += `〖${i}〗${T('a:eq(1)')}\n${T('a:eq(0)')}・${String(T('span')).replace(/更新\s*/, '')}\n&shy;\n`;
};
java.put("url", site[t].select('a:eq(1)').attr('href'));
`&nbsp;🌸当前线路〖${t}〗🌸\n&shy;\n` + list + J.select('#intro').html();

ruleToc.chapterList:
ol@a
@js:
J = result;
list = [];
for (i = 0; i < J.length; ++i) {
  u = J[i].attr('href');
  list.push({
    name: J[i].text(),
    url: u,
    num: u.split('/')[3].slice(0, -5)
  });
};
Rule = (a, b) => a.num - b.num;
list.sort(Rule);
