ruleContent.content:
class.wrap@tag.script.0@html
<js>
result = "https:" + result.match(/initTxt\(\"(.*?)\"/)[1] + ",{\"charset\":\"utf-8\"}";
result = java.ajax(result);
var replacestr = "," + result.match(/replace\"\:\{(.*?)\}/)[1];
var str = String(replacestr);
var str1 = str.match(/\:\"(.*?)\"/g);
var str2 = str.match(/,\"(.*?)\"\:/g);
var pattern = /\"|\:|\,|;/g;
for (var i = 3; i< str1.length; i++) {
    str1[i] = str1[i].replace(pattern, '');
    str2[i] = str2[i].replace(pattern, '');
    result = result.replace(str1[i], str2[i]);
}
var sohstr = '';
var stxstr = '';
var unicode = '\\u';
var pattern1 = /.*?content\":\"|\",\"replace.*/g;
result = result.replace(sohstr, unicode);
result = result.replace(stxstr, unicode);
result = String(result);
result = result.replace(pattern1, '');
result = JSON.parse('"' + result + '"');
result = result.replace(/;/g, '');
</js>
