ruleExplore.kind:
creation_status&&category_v2 
@js:
var jso=String(result[1]);
 var jsonString = String(jso)
        .replace(/\\\"/g, '"')       // 修复双引号转义
        .replace(/\\'/g, "'")        // 修复单引号转义
        .replace(/\\\\/g, '\\')      // 修复反斜杠转义
        .replace(/\\\n/g, '\n')       // 修复换行符转义
        .replace(/\\\r/g, '\r')      // 修复回车符转义
        .replace(/\\t/g, '\t')     // 修复制表符转义
           .replace(/"{/g, '{')       // 修复花括号转义
    .replace(/}"/g, '}');      // 修复花括号转义

data = JSON.parse(jsonString );
var names = data.map(item => item.Name);
var state='';
switch(parseInt(result[0])){
case 4:case 0: state='完结';break;
case 1: state='连载中';break;
case -1: state='已断更';break;
default: state='已下架';break;
}
var list=[];
list.push(state);
list.push(names );

result=list.toString();

