ruleToc.chapterName:
@js:
r = `{{@p.0@text}}`.split(' - ');
o = r[2].replace(/\D/g, '');
s = java.getString('p!0:-1@html');
t = `{{@p.-1@text}}`.split(' - ');
q = t[1];
q = q == null ? "": "<br>更新：" + q.replace(/^\S*\s/g, '');
java.put("url", `data:;base64,${java.base64Encode(s + q)},{"type":"NRHSYD"}`);
java.put("info", r[0] + `・${t[0]}・` + r[1]);
x = "";
for (i = 0; i < o; ++i) {
  x += "★";
};
x;

