loginUrl:
function login() {
	result = source.getLoginInfoMap()
	loginButton()
}

function loginButton() {
	request = JSON.stringify({
		'body': "loginName=" + result.get("邮箱") + "&password=" + result.get("密码"),
		'method': 'POST'
	})
	response = JSON.parse(java.ajax("https://www.uaa.com/api/console/app/login," + request))
	if ( response.result == "success" ) {
		java.toast("登录成功！\n" + JSON.stringify(response.model));
		source.setVariable(response.model.token);
	} else {
		java.toast(response.result + "\n" + response.msg);
	}
}

function sendCheckCode() {
	request = {
		"body": 'email=' + result.get("邮箱"),
		"method": "POST"
	};	
	request = JSON.stringify(request);
	response = java.ajax('https://www.uaa.com/api/console/app/sendCheckCode,' + request);
	java.toast(response);
}

function register(){
	body = "loginName=" + result.get("邮箱") + "&password=" + result.get("密码") + "&checkCode=" + result.get("注册验证码") + "&recommendCode=" + result.get("邀请码")
	request = JSON.stringify({
		"body": body,
		"method": "POST"
	})
	response = JSON.parse(java.ajax('https://www.uaa.com/api/console/app/register,' + request))

	if ( response.result == "success" ) {
		java.toast("注册成功！已自动登录！\n" + JSON.stringify(response.model));
		source.setVariable(response.model.token);
	} else {
		java.toast(response.result + "\n" + response.msg);
	}
}

