loginUrl:
function login() {
	result = source.getLoginInfoMap()
	loginButton()
}
var url = baseUrl .split('/')[0] + '//' + baseUrl .split('/')[2];

function loginButton() {
	request = JSON.stringify({
		'body': "loginName=" + result.get("邮箱") + "&password=" + result.get("密码"),
		'method': 'POST'
	})
	response = JSON.parse(java.ajax(url + "/login," + request))
	if ( response.result == "success" ) {
		java.toast("登录成功！\n" + JSON.stringify(response.model));
                cookie.setCookie(baseUrl,"token=" + response.model.token);
	} else {
		java.toast(response.result + "\n" + response.msg);
	}
}

