		push(title,$$(a,b,c),0.25);
			});

if(style==0){
		arrb.map([title,b]=>{
				d(title,a,b,c);
				arra.map((title,a)=>arrc(title,a,b));
			});
	}
else{
		arra.map((title,a)=>{
				d(title,a,b,c);
				arrb.map([title,b]=>arrc(title,a,b));
			});
	}
JSON.stringify(sort);
