ruleContent.content:
@js:
u=java.getStringList('#buy_chapter@a@href');
if(u.length>1){
	src=java.ajax(source.key+u[0])
}
t=Number(cache.get(source.key+'t'))
now=Math.floor(Date.now()/1000)
c=Number(cache.get(source.key))
lock=false
if(c>=10){
	if(now-t>60){
	 cache.put(source.key+'t',now)
	 cache.delete(source.key)
	 c=0
 }else{
	 lock=true
	}
}
if(lock){
	//java.getVerificationCode('https://aifoxs.gitee.io/img/tx.jpg')
	java.toast('车速过快')
}else{
	c+=1
	cache.put(source.key,c)
	iv=src.match(/iv":"(.+?)"/)[1]
	key=java.hexDecodeToByteArray('a15e2a7afc8c34a526efb8c6495e7b26')
	iv=java.base64DecodeToByteArray(iv)
	enc=src.match(/enc":"(.+?)"/)[1]
	enc=java.base64DecodeToByteArray(enc)
	c=java.createSymmetricCrypto('AES/CBC/PKCS5Padding',key, iv)
	c.decryptStr(enc)
}

