jsLib:
function errorReg() {
  return /(java.net.+Exception)|(okhttp.+Exception)|(error code: 1015)|(503 Service Temporarily Unavailable)|(Parse error.+in.+wwwroot.+php)|(_cf_chl_opt|challenges.css)|(<center.+>.+不支持)|(章节内容不支持该浏览器显示)/gi
}
function ajax(url) {
  const { java, cookie } = this;
  let content = "";
  let time=1;
  	content = java.ajax(url);
  	while (String(content).match(errorReg())) {
    if (time>10) {
  	    java.toast("重试10次后仍请求失败："+url)
  	    throw("重试10次后仍请求失败："+url+content)
  	    break;
    	}
    	if(!!String(content).match(/不支持/gi)) {
      //java.log(cookie.getCookie(url));
      cookie.replaceCookie(url,java.webView(null,url,"document.cookie"));
    	}
    	if (!!String(content).match(/_cf_chl_opt|challenges.css/gi)) {
    		 i = 1;
      	while (!!String(content).match(/_cf_chl_opt|challenges.css/gi)) {
	       java.log('盾');
	       java.log(content);
        if (i <= 1) {
          java.toast("哦呼，五秒盾，正在尝试静默破盾ing……");
          content = java.webView(null,url,null);
        } else {
          java.toast("啊哦，静默破盾好像不管用，试试手动过校验吧！");
          content = java.startBrowserAwait(url,"加载完毕后点完成，此页面可能会弹出多次").body();
        }
        i++;
      }
      continue;
    }
  		 java.log("请求失败："+url)
  		 java.log("重试"+time+"次")
  		 let t=new Date().getTime()
    while (new Date().getTime() - t < 500) { }
    if (String(content).match(/1015|503|php/gi)) {
    	  while (new Date().getTime() - t < 5000) { }
    	}
  		 content = java.ajax(url);
  		 time++;
  	}
  	return String(content);
}

